import json

# decimal to degrees (a°b'c")


def __dec2deg(dec):
    dec = float(dec)
    a = int(dec)
    a_new = (dec-float(a)) * 60.0
    b_rounded = int(round(a_new))
    b = int(a_new)
    c = int(round((a_new-float(b))*60.0))
    out = (f'{a:02d}° {b:02d}\' {c:02d}"')
    return str(out)


